INSERT INTO `present` VALUES ('GY-JP-026', '马到成功', '01', '010008', '640', null, '<p>寓意卖点<br />\r\n1。马到成功一词是马年春节晚会上最受欢迎的吉祥语，是一份祝福和渴望。<br />\r\n2、产品富有雕塑感配合岩山象征坚忍不拔，骏马顺势腾空奔驰，显示胸怀信念。<br />\r\n3。造型充满力量感寓意积极进取，不畏艰险，义无反顾，奔向成功的未来。<br />\r\n4、山随马势更显该款产品的整体设计更加完美。</p>\r\n<p><br />\r\n<span style=\"font-size: small\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 适合群体：企业开业庆典礼品、家居摆饰等</span></p>', '_29db27f30129db29b38c0003.jpg', '_29db27f30129db29b36d0002.jpg', '每件', '153*125*265MM', '0', '', '礼品盒', '', '琉璃+木座', '工艺品', '500', '', null, '0', '0', '2010-07-16 20:10:30');
