INSERT INTO `present` VALUES ('GY-LL-012', '双龙戏珠茶叶罐', '01', '010001', '195', null, '<p>&nbsp;&nbsp;&nbsp;<span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp; 三江合流通入海，龙者为尊盛世来。戏说人间天下事，珠联璧合福运来</span></p>', '_29d028150129d02817520001.jpg', '_29d028150129d028158c0000.jpg', '每件', '100X150', '0', '', '礼品盒', '', '琉璃', '琉璃', '500', '', null, '0', '0', '2010-07-14 16:52:55');
INSERT INTO `present` VALUES ('GY-LL-013', '四季茶壶（夏）', '01', '010001', '500', null, '<p><span style=\"font-size: small\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;琉璃具有水晶一样的玲珑，又有金属般的质地，有云霞一样流动的色彩，又有与一样的温润，故被视为佛家七宝，道家极品。</span></p>\r\n<p><span style=\"font-size: small\"><span style=\"color: #ff6600\">如何欣赏琉璃作品</span><span style=\"color: #ff6600\"><br />\r\n</span>1.琉璃作品置于自然光或底光中，呈现出不同的质感，琉璃材质吸收不同角度的光影折射。释放出多变的视觉效果。<br />\r\n2.琉璃作品主题形态的表现，结合不同的色调，无论剔透，无论明暗，在作品中用琉璃的语言诉说琉璃文化的经典。<br />\r\n</span></p>\r\n<p><span style=\"font-size: small\"><span style=\"color: #ff6600\">如果保养琉璃作品<br />\r\n</span>1.保持常温，不可以物理的方式对其进行加热或冷却。<br />\r\n2.平面光滑处，不宜直接放置于玻璃桌面，不可碰撞或磨擦移动，以免出现表层划伤。<br />\r\n3.宜用纯净水擦拭，保持琉璃表面之光泽与干净，切不可沾上油渍异物等。<br />\r\n4.避免与腐蚀性试剂接触。</span></p>', '_29d0e0e20129d0e0e9290001.jpg', '_29d0e0e20129d0e0e2ff0000.jpg', '每件', '105x85x50', '0', '', '礼品盒', '', '琉璃', '琉璃', '500', '', null, '1', '0', '2010-07-14 20:18:37');
INSERT INTO `present` VALUES ('GY-LL-014', '四季茶壶（秋）', '01', '010001', '499', null, '<p><font size=\"2\">&nbsp;&nbsp;&nbsp;&nbsp; 琉璃具有水晶一样的玲珑，又有金属般的质地，有云霞一样流动的色彩，又有与一样的温润，故被视为佛家七宝，道家极品。</font></p>\r\n<p><span style=\"font-size: small\"><span style=\"color: #ff6600\"><font size=\"2\">如何欣赏琉璃作品</font></span><span style=\"color: #ff6600\"><br />\r\n</span><font size=\"2\">1.琉璃作品置于自然光或底光中，呈现出不同的质感，琉璃材质吸收不同角度的光影折射。释放出多变的视觉效果。<br />\r\n2.琉璃作品主题形态的表现，结合不同的色调，无论剔透，无论明暗，在作品中用琉璃的语言诉说琉璃文化的经典。<br />\r\n</font></span></p>\r\n<p><span style=\"font-size: small\"><font size=\"2\"><span style=\"color: #ff6600\">如果保养琉璃作品<br />\r\n</span>1.保持常温，不可以物理的方式对其进行加热或冷却。<br />\r\n2.平面光滑处，不宜直接放置于玻璃桌面，不可碰撞或磨擦移动，以免出现表层划伤。<br />\r\n3.宜用纯净水擦拭，保持琉璃表面之光泽与干净，切不可沾上油渍异物等。<br />\r\n4.避免与腐蚀性试剂接触。</font></span></p>', '_29d0e0e20129d0e3d3ed0003.jpg', '_29d0e0e20129d0e3d3ce0002.jpg', '每件', '105x85x50', '0', '', '礼品盒', '', '琉璃', '', '500', '', null, '0', '0', '2010-07-14 20:17:58');
