INSERT INTO `present` VALUES ('GY-TD-003', '鸟语花香', '01', '010003', '264', null, '<p><span style=\"font-size: small\"><span style=\"font-family: 楷体_GB2312; color: black; language: zh-CN; mso-ascii-font-family: 楷体_GB2312; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">陶渊明著</span><span style=\"font-family: 楷体_GB2312; color: black; language: en-US; mso-ascii-font-family: 楷体_GB2312; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">《</span><span style=\"font-family: 楷体_GB2312; color: black; language: zh-CN; mso-ascii-font-family: 楷体_GB2312; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">桃花源记</span><span style=\"font-family: 楷体_GB2312; color: black; language: en-US; mso-ascii-font-family: 楷体_GB2312; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">》</span><span style=\"font-family: 楷体_GB2312; color: black; language: zh-CN; mso-ascii-font-family: 楷体_GB2312; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">：</span><span style=\"font-family: Arial; color: black; language: zh-CN; mso-ascii-font-family: Arial; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">&ldquo;</span><span style=\"font-family: 楷体_GB2312; color: black; language: zh-CN; mso-ascii-font-family: 楷体_GB2312; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">忽逢桃花林，夹岸数百步，中无杂树，芳草鲜美，落英缤纷</span><span style=\"font-family: Arial; color: black; language: zh-CN; mso-ascii-font-family: Arial; mso-fareast-font-family: 楷体_GB2312; mso-bidi-font-family: +mn-cs; mso-color-index: 1; mso-font-kerning: 12.0pt\">&rdquo;</span><span style=\"font-family: 楷体_GB2312; color: black; language: zh