INSERT INTO `present` VALUES ('GY-TZ-015', '和美', '01', '010005', '650', null, '<p>华夏瑰宝&mdash;国礼中国红。中国红瓷因高贵的品质，被人们作为富贵、吉祥、实用、高贵、美观的象征，自2006年以来，中国红瓷被外交部多次选定为国礼用瓷，牡丹天球瓶被赠送中非论坛与会非洲40多个国家元首，镂空玉壶春，彩金圆盘等被赠送东盟博览会十一国国家元首，国色天香赏瓶等多次赠送外国友人，中国红瓷在国内外享有空前盛誉。</p>', '_29d522da0129d55522030017.jpg', '_29d522da0129d55521e40016.jpg', '每件', '', '0', '', '礼品盒', '', '红瓷', '红瓷', '500', '', null, '0', '0', '2010-07-15 17:00:13');
