INSERT INTO `present` VALUES ('GY-TZ-017', '雄视天下地球仪', '01', '010005', '265', null, '<p>运筹于帷幄之中，决胜于千里之外，世界万物成竹在胸，彰显主人胸怀千万里的宏大志向；商场如战场，愈演愈烈，地球是平的，世界是平的，资讯传递需火的倍速，掌握世界格局，精准布局，方可百战百胜。</p>', '_29d522da0129d562b0a7001b.jpg', '_29d522da0129d562b088001a.jpg', '', '140*205', '0', '', '礼品盒', '', '骨瓷', '红瓷', '500', '', null, '0', '0', '2010-07-15 17:15:01');
