INSERT INTO `present` VALUES ('PP-DU-012', '傲仕杯', '07', '070004', '85', null, '<p>1、突破性的采用不锈钢内胆与TPE软胶相结合的工艺结构；TPE软胶非常环保、触感柔软，常适用于婴儿产品；<br />\r\n2.杯子保温隔热，即使倒入沸水，杯身触感仍然恒温舒适。<br />\r\n3.有兰、红、橙等多种颜色选择。<br />\r\n4.推荐理由：傲仕杯保软胶杯系列全新引入美国设计元素，外形图纹设计紧贴时尚热点，这次新款杯子的创意是&ldquo;把幸福握在手中&rdquo;，触感犹如肌肤般细腻预柔滑，保温隔热，即使倒入沸不，握在手中仍是&ldquo;温暖常驻&rdquo;。<br />\r\n&nbsp;</p>', '_2a3ad0a4012a3b37dfa7003d.jpg', '_2a3ad0a4012a3b37df88003c.jpg', '每个', '', '0', '', '彩盒', '', '不锈钢、环保软胶', '', '500', '', null, '0', '0', '2010-08-04 11:49:31');
