INSERT INTO `present` VALUES ('SM-XK-012', '11寸数码相框', '05', '050002', '500', null, '<p>11寸高清晰数字宽屏<br />\r\n1.支援内置16 MB-2GB NAND FLASH存储。<br />\r\n2.支持时钟，闹钟，及万年历功能。<br />\r\n3.支持USB HOST 1.1 连接外部USB存储设备，如U&nbsp;&nbsp; 盘，MP3播放器等。<br />\r\n4.支持USB DEVICE 2.0，可连接外部计算机进行文件上传下载。<br />\r\n5.支持3200万像素的照片，保证图像快速浏览。<br />\r\n6.支持数字显示屏最大显示分辨率1024x768。<br />\r\n7.支持7种国家的语言菜单。<br />\r\n8.支持SD/MMC卡，CF卡，MS/MS DUO卡，XD卡，MS卡读写。<br />\r\n9.支持卡间文件拷贝及文件删除操作。<br />\r\n10.支持AV输出（可扩展S-VIDEO及YPbPr输出）。<br />\r\n11.支持JPEG及BMP文件浏览，放大，及旋转。<br />\r\n12.支持根据16/9或4/3显示屏实际分辨率对图片进行缩放或切图处理以适应原图长宽比率，保证显示图像不变形。<br />\r\n13.支持JPEG缩略图（Thumb）浏览。<br />\r\n14.支持JPEG硬件译码。<br />\r\n15.最大14M BYTES的JPEG文件。最大水平或垂直方向8K像素，最大总像素32M的JPEG文件。最大14M BYTES的BMP文件。最大水平或垂直方向8K像素，最大总像素2M的BMP文件。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />\r\n16.支持MP3及WMA文件播放。</p>', '_2a20eadf012a20fe57b60013.jpg', '_2a20eadf012a20fe57970012.jpg', '每个', '', '0', '', '彩盒', '', '', '', '500', '', null, '0', '0', '2010-07-30 09:36:33');
