INSERT INTO `yyc_news` VALUES ('f20e196436a7f57c0136a9377aa40001', '张姗姗与北新实幼座谈', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3月8日是专属于女性同胞的日子。高级培训师、育英才教育机构总裁张珊珊女士到北部新区实验幼儿园参加座谈，为老师讲解有关幼儿教师精神操守与职业规划的专题讲座。</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 只有真正热爱幼教事业，热爱幼儿，视园所如家才能够保证个人职业发展与园所发展的高度一致，在园所提供的发展平台上实现个人价值最大化。</p>\r\n<p><img alt=\"\" src=\"imgs/upload/20120308105602_590.jpg\" /></p><br>\r\n<p><img alt=\"\" src=\"imgs/upload/20120308105707_859.jpg\" /></p>', '0', '2012-03-09 09:07:08');
INSERT INTO `yyc_news` VALUES ('f20e196436a7f57c0136a93cda7f0002', '张姗姗与北新实幼苏家坨园座谈', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2012年3月8日中午，在北新实幼苏家坨园部精心安排下，高级培训师、育英才教育机构总裁张珊珊来与苏家坨园美丽的女教师们聚在一起，为女教师们带来的题为《幼儿教师的师德操守与职业能力》的职业生涯规划培训。</p>\r\n<img src=\"imgs/upload/20120308175415_264.jpg\"><br>\r\n<img src=\"imgs/upload/20120308175435_715.jpg\"><br>', '0', '2012-03-09 09:06:39');
